package projectfor10259;

public class pattrnexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 5; i>=1; i--)
		{char c = 'A';
			for(int j=1; j<=i; j++)
			{
				System.out.print(c);
				c++;
			}
			System.out.println("");
		}

	}

}
// i = 5 j = 1,2,3,4,5
